# -*- coding: utf-8 -*-import cv2# load pre-trained cascade classifierface_cascade = cv2.CascadeClassifier('haarcascade_frontalface_default.xml')eye_cascade = cv2.CascadeClassifier('haarcascade_eye.xml')# load imageimg = cv2.imread('obama.jpg')img_grey = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY)# run face detection# detectMutliScale(img, scale factor, number of neighbours)face_coords = face_cascade.detectMultiScale(img_grey, 1.1, 8)# show facesfor face_coord in face_coords:    x, y, w, h = face_coord    # draw the rectangle on main image    cv2.rectangle(img, (x, y), (x+w, y+h), (0,0,255), 2)    # run eye detection eye_coords = eye_cascade.detectMultiScale(img_grey, 1.07, 5)print(eye_coords)for eye_coord in eye_coords:    x, y, w, h = eye_coord    cv2.rectangle(img, (x, y), (x+w, y+h), (255,0,0), 2)    # shows image and waits for keypress    cv2.imshow('faces', img)cv2.waitKey(0)cv2.destroyAllWindows()