# -*- coding: utf-8 -*-import cv2# load pre-trained cascade classifierface_cascade = cv2.CascadeClassifier('haarcascade_frontalface_default.xml')# load imageimg = cv2.imread('obama.jpg')img_grey = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY)# run face detection# detectMutliScale(img, scale factor, number of neighbours)face_coords = face_cascade.detectMultiScale(img_grey, 1.1, 8)print(face_coords)# show facesi = 0for face_coord in face_coords:    x, y, w, h = face_coord    # draw the rectangle on main image    cv2.rectangle(img, (x, y), (x+w, y+h), (0,0,255), 2)        #extract faces from main image    face = img[y:y+h, x:x+w]        # show face0 and face1    cv2.imshow('face{}'.format(i), face)    i += 1    # shows image and waits for keypress    cv2.imshow('faces', img)cv2.waitKey(0)cv2.destroyAllWindows()